async function main() {
  console.log(`Enriching ${enrichments.length} artworks with descriptions and FAQs...\n`);

  let updated = 0;
  let notFound = 0;
  let errors = 0;

  for (const enrichment of enrichments) {
    try {
      const existing = await prisma.artwork.findUnique({
        where: { slug: enrichment.slug },
      });

      if (!existing) {
        console.log(`✗ Not found: ${enrichment.slug}`);
        notFound++;
        continue;
      }

      await prisma.artwork.update({
        where: { slug: enrichment.slug },
        data: {
          description: enrichment.description,
          faqs: enrichment.faqs,
          updatedAt: new Date(),
        },
      });

      console.log(`✓ Updated: ${enrichment.slug}`);
      updated++;
    } catch (error: any) {
      console.error(`✗ Error updating ${enrichment.slug}:`, error.message);
      errors++;
    }
  }

  console.log(`\n--- Summary ---`);
  console.log(`Updated: ${updated}`);
  console.log(`Not found: ${notFound}`);
  console.log(`Errors: ${errors}`);
}

main()
  .catch(console.error)
  .finally(() => prisma.$disconnect());
