async function main() {
  console.log(`Updating ${updates.length} artworks...\\n`);
  let success = 0, fail = 0;

  for (const u of updates) {
    try {
      const r = await prisma.artwork.update({
        where: { slug: u.slug },
        data: { description: u.description, faqs: u.faqs, updatedAt: new Date() }
      });
      console.log(`✓ ${r.title}`);
      success++;
    } catch (e) {
      console.error(`✗ ${u.slug}: ${e.message}`);
      fail++;
    }
  }

  console.log(`\\n=== Done: ${success} updated, ${fail} failed ===`);
}

main().catch(console.error).finally(() => prisma.$disconnect());
